mapper.select:
select step, x, y
from stopby
where seq = #{seq}


select b.*
from(
select rownum rn, a.*, (select count(mseq) from memo where seq = a.seq) memocnt
from(
select b.seq, b.user_id, b.bcode, b.name, b.email, b.subject, b.content , b.hit,
decode(to_char(sysdate,'yymmdd'), to_char(b.logtime, 'yymmdd'), 
to_char(b.logtime, 'hh24:mi:ss'), to_char(b.logtime, 'yy.mm.dd')) logtime
from board b
where
b.bcode = #{bcode}


        and b.subject like '%'||#{word}||'%'


        and b.${key} = #{word}


order by b.seq desc
) a
where rownum <= #{end}
) b
where b.rn > #{start}
