mapper.update:
     update users
		set type='3' 
		where user_id =#{id}

     update users
		set type='1' 
		where user_id =#{id}

mapper.select:
   select d.*
	from(
		select rownum rn, c.*
		from(
			select user_id userId ,name ,email,tel
			from users
			where type =#{bcode}

		
            
                and name like '%'||#{word}||'%'
            
            
                and ${key} = #{word}
            
         
			order by user_id desc
			) c
		where rownum <= #{end} 
		) d
	where rn > #{start}

    select d.*
	from(
		select rownum rn, c.*
		from(
			select user_id userId ,name ,email,tel
			from users
			where type =#{bcode}

		
            
                and name like '%'||#{word}||'%'
            
            
                and ${key} = #{word}
            
         
			order by user_id desc
			) c
		where rownum <= #{end} 
		) d
	where rn >= #{start}
