mapper.select:
select count(email)
from users
where email=#{email}

mapper.insert:
update users
set state='2'
where email=#{email}

mapper.select:
select user_id userId, email, password, tel, gender, type, reg_date regDate, state
from users
where email=#{email} and password=#{password}

mapper.update:
update users
set password=#{password},state='3'
where email=#{email}

update users
set password=#{password},state='2'
where email=#{email}
